    val listData: ArrayList<Club>
        get() {
            val list = arrayListOf<Club>()
            for (position in teamName.indices) {
                val team = Club()
                team.name = teamName[position]
                team.detail = teamDetail[position]
                team.photo = teamImage[position]
                team.information = teamInformation[position]
                list.add(team)
            }
            return list
